import React, {useState} from 'react'
import {useDispatch} from 'react-redux'
import { useHistory } from 'react-router-dom'
import GoogleLogin from 'react-google-login'
import { AUTH } from '../constants/actionTypes';
import {signUp, signIn} from '../actions/auth'

const initialState ={
    firstName      : '',
    lastName       : '',
    email          : '',
    password       : '',
    confirmPassword: ''

}

const Auth = () => {
    const dispatch                        = useDispatch()
    const history                         = useHistory()
    const [showPassword, setShowPassword] = useState(false)
    const [isSignUp, setIsSignUp]         = useState(false)
    const [formData, setFormData]         = useState(initialState)

    const handleSubmit = (e) => {
        e.preventDefault()
        
        if(isSignUp) {
            dispatch(signUp(formData, history))
        } else {
            dispatch(signIn(formData, history))
        }
    }
    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        })
    }
    const handleShowPassword = () => setShowPassword((prevShowPassword) => !prevShowPassword)

    const switchMode = () => {
        setIsSignUp((prevIsSignUp) => !prevIsSignUp)
        setShowPassword(false)
    }

    const googleSuccess = async (res) => {
        const result = res?.profileObj;
        const token  = res?.tokenId;
        // console.log(result, token)

        try {
            dispatch({ 
                type: AUTH,
                data: { 
                    result, 
                    token 
                } 
            });
            history.push('/')
        } catch (error) {
            console.log(error)
        }
    }

    const googleFailure = () => {
        console.log('gogole sign in failed please try again')
    }

    return (
        <div className="main-content">
        
            <div className="header bg-gradient-primary py-7 py-lg-8 pt-lg-9">
                <div className="separator separator-bottom separator-skew zindex-100">
                    <svg x="0" y="0" viewBox="0 0 2560 100" preserveAspectRatio="none" version="1.1" xmlns="http://www.w3.org/2000/svg">
                        <polygon className="fill-default" points="2560 0 2560 100 0 100"></polygon>
                    </svg>
                </div>
            </div>
            
            <div className="container mt--8 pb-5">
                <div className="row justify-content-center">
                    <div className="col-lg-5 col-md-7">
                        <div className="card bg-secondary border-0 mb-0">
                            <div className="card-header bg-transparent pb-5 text-center" style={{backgroundImage: 'url("/assets/img/profile-box-bg.png")'}}>
                                {/* <img src="/assets/img/profile-box-bg.png" style={{width: '250px'}} alt="logo"/> */}
                                <div className="text-muted text-center mt-2 mb-3">
                                    <h1>SIGN IN</h1>
                                </div>
                            </div>
                            <div className="card-body px-lg-5 py-lg-5">
                                <form onSubmit={handleSubmit}>
                                    <div className="form-group mb-3">
                                        <div className="input-group input-group-merge input-group-alternative">
                                            <div className="input-group-prepend">
                                                <span className="input-group-text"><i className="ni ni-email-83"></i></span>
                                            </div>
                                            {/* <label>{this.state.email}</label> */}
                                            <input className="form-control" placeholder="email" type="text" id="email" onChange={handleChange} value="email" />
                                        </div>
                                    </div>
                                    <div className="form-group">
                                        <div className="input-group input-group-merge input-group-alternative">
                                            <div className="input-group-prepend">
                                                <span className="input-group-text"><i className="ni ni-lock-circle-open"></i></span>
                                            </div>
                                            <input className="form-control" placeholder="Password" type="password" id="password" />
                                        </div>
                                    </div>
                                    <div className="text-center">
                                        <button type="submit" className="btn my-4" name="dashboard_login">Sign in</button>
                                    </div>
                                </form>
                                
                                <GoogleLogin
                                        clientId = "670432850281-pl1qai55svsfvkr0mi94ca2lp0h3o0re.apps.googleusercontent.com"
                                        render   = {(renderProps) => (
                                            <button onClick = {renderProps.onClick} disabled = {renderProps.disabled}>Google Sign In</button>
                                        )}
                                        onSuccess    = {googleSuccess}
                                        onFailure    = {googleFailure}
                                        cookiePolicy = "single_host_origin"
                                    />
                            </div>
                        </div>
                        <div className="row mt-3">
                            <div className="col-6">
                                <a href="link.php" className="text-light" data-toggle="modal" data-target="#modal-form"><small>Forgot password?</small></a>
                                <div className="modal fade" id="modal-form" tabindex="-1" role="dialog" aria-labelledby="modal-form" aria-hidden="true">
                                    <div className="modal-dialog modal- modal-dialog-centered modal-sm" role="document">
                                        <div className="modal-content">
                                            <div className="modal-body p-0">
                                                <div className="card bg-secondary border-0 mb-0">
                                                    <div className="card-header bg-transparent">
                                                        <div className="text-muted text-center mt-2 mb-3"><small>Request a reset email</small></div>
                                                    </div>
                                                    <div className="card-body px-lg-5 py-lg-5">
                                                        <div className="form-group">
                                                            <input type="email" className="form-control" id="email" value="admin@admin.com" hidden />
                                                        </div>
                                                        <div className="form-group mb-3">
                                                            <div className="input-group input-group-merge input-group-alternative">
                                                                <div className="input-group-prepend">
                                                                    <span className="input-group-text"><i className="ni ni-lock-circle-open"></i></span>
                                                                </div>
                                                                <input className="form-control" placeholder="Email" type="email" id="email" />
                                                            </div>
                                                        </div>
                                                        <div className="text-center pt-30px">
                                                            <button className="btn btn-secondary" onclick="edit_password()">Reset Password</button>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Auth
