import React from 'react'
import moment from 'moment'
import { useDispatch } from 'react-redux'
import { deleteBlog, likeBlog } from '../../actions/blogs'

const Blog = ({blog, setCurrentId}) => {
    const dispatch = useDispatch()
    const user     = JSON.parse(localStorage.getItem('profile'))

    const Likes = () => {
        if(blog.likes.length > 0) {
            return blog.likes.find((like) => like === (user?.result?.googleId || user?.result?._id))
            ? (
                <div>{blog.likes.length > 2 ? `You and ${blog.likes.length - 1} others` : `${blog.likes.length} like${blog.likes.length > 1 ? 's' : ''}`}</div>
            ) : (
                <div>{blog.likes.length} {blog.likes.length === 1 ? 'Like' : 'Likes'}</div>
            )
        }
        return <div>Like</div>
    }

    return(
        <div style={{margin:'50px', backgroundColor:'grey'}}>
            <img style={{width :'100px'}} src={blog.selectedFile} alt="blogimage" />
            <h1>{blog.title}</h1>
            <h3>{blog.name}</h3>
            <h5>{moment(blog.createdAt).fromNow()}</h5>
            
            {(user?.result?.googleId === blog?.creator || user?.result?._id === blog?.creator) && (
            <button onClick={() => setCurrentId(blog._id)}>edit</button>
            )}
            
            <p>{blog.tags ? blog.tags.map((tag) => `#${tag} `) : 'No Tags'}</p>
            <p>{blog.message}</p>
            <div>
                <button disabled={!user?.result} onClick={() => dispatch(likeBlog(blog._id))}>
                    <Likes />
                </button>
                {(user?.result?.googleId === blog?.creator || user?.result?._id === blog?.creator) && (
                    <button onClick={() => dispatch(deleteBlog(blog._id))}>
                    Delete
                    </button>
                )}
            </div>
        </div>
    )
}

export default Blog
