import {useDispatch, useSelector} from 'react-redux'
import React, {useState, useEffect} from 'react'
import FileBase from 'react-file-base64'
import moment from 'moment'

import { getUsers, updateUser, deleteUser, changeUserStatus } from '../actions/users'

const Users = () => {

    const [currentId, setCurrentId] = useState(null)
    const dispatch                  = useDispatch()
    const user                      = JSON.parse(localStorage.getItem('profile'))
    const users                     = useSelector((state) => state.users)
    const editUser                  = useSelector((state) => currentId ? state.users.find((e) => e._id === currentId) : null)

    const [userData, setUserData] = useState({
        title       : '',
        message     : '',
        tags        : '',
        selectedFile: ''
    })


    useEffect(() => {
        if(editUser) setUserData(editUser)
    },[editUser])
    
    useEffect(() => {
        dispatch(getUsers())
      }, [dispatch])

    const handleSubmit = (e) => {
        e.preventDefault()

        if(currentId){
            dispatch(updateUser(currentId, {...userData, name: user?.result?.name}))
        } else {
            dispatch(createUser({...userData, name: user?.result?.name}))
        }
        clear()
    }

    const clear = () => {
        setCurrentId(null)
        setUserData({
            title       : '',
            message     : '',
            tags        : '',
            selectedFile: ''
        })
    }

    if(!user?.result?.name) {
        return(
            <div>
                Please Sign In To continue
            </div>
        )
    }

    // return(
    //     <div>
    //     </div>
    // )
}


export default Users
